mapper.select:

select

  distinct


from trade_order

  


  order by ${orderByClause}

 
   ${limitByClause}


  select sum(total_money) as total_moneys from trade_order WHERE #{days} = (TO_DAYS(NOW()) -
       TO_DAYS(gmt_create)) and shop_id=#{shopId}
       GROUP BY shop_id ,shop_name
